#include&lt;iostream&gt;

struct node 
{
	int key;
	struct node * ptr_next;
};

struct stack 
{
	struct node* top=nullptr;
	int size=0;
};

void push(struct stack &amp;st, int key)
{
	struct node *temp = new node();
	temp-&gt;key = key;

	temp-&gt;ptr_next=st.top;
	st.top = temp;
	st.size++;
}

